mapper.select:
select a.id as "id", a.type as "type", a.title as "title", a.intro as "intro", a.content as "content", a.read_num as "read_num", a.status as "status", a.order_by as "order_by", a.remark as "remark", a.create_time as "create_time", a.create_user as "create_user", a.create_organize as "create_organize", a.update_user as "update_user", a.update_time as "update_time" from gencode_mycontent a where 1=1
and a.status = #{obj.status}
and a.type = #{obj.type}
and a.title like concat('%',concat(#{obj.title},'%'))
order by a.status+0 asc, a.create_time desc

select a.id as "id", a.type as "type", a.title as "title", a.intro as "intro", a.content as "content", a.read_num as "read_num", a.status as "status", a.order_by as "order_by", a.remark as "remark", a.create_time as "create_time", a.create_user as "create_user", a.create_organize as "create_organize", a.update_user as "update_user", a.update_time as "update_time" from gencode_mycontent a where 1=1
and a.status = #{status}
and a.type = #{type}
and a.title like concat('%',concat(#{title},'%'))
order by a.status+0 asc, a.create_time desc
