mapper.select:
select a.parent_id as "parent_id", ifnull(b.num,0) as "child_num", p.name as "parent_name", a.id as "id", a.type as "type", a.name as "name", a.short_name as "short_name", a.status as "status", a.order_by as "order_by", a.remark as "remark", a.create_time as "create_time", a.create_user as "create_user", a.create_organize as "create_organize", a.update_user as "update_user", a.update_time as "update_time" from gencode_mytree a left join gencode_mytree p on a.parent_id=p.id left join ( select count(*) as "num", a.parent_id as "parent_id" from gencode_mytree a group by a.parent_id ) b on a.id=b.parent_id where 1=1
and a.status = #{obj.status}
and a.type = #{obj.type}
and a.name like concat('%',concat(#{obj.name},'%'))
and a.parent_id = #{obj.parent_id}
order by a.status+0 asc, a.create_time desc

select a.parent_id as "parent_id", ifnull(b.num,0) as "child_num", a.id as "id", a.type as "type", a.name as "name", a.short_name as "short_name", a.status as "status", a.order_by as "order_by", a.remark as "remark", a.create_time as "create_time", a.create_user as "create_user", a.create_organize as "create_organize", a.update_user as "update_user", a.update_time as "update_time" from gencode_mytree a left join ( select count(*) as "num", a.parent_id as "parent_id" from gencode_mytree a group by a.parent_id ) b on a.id=b.parent_id where 1=1
and a.status = #{status}
and a.type = #{type}
and a.name like concat('%',concat(#{name},'%'))
and a.parent_id = #{parent_id}
order by a.status+0 asc, a.create_time desc
