mapper.select:
select a.id as "id", a.type as "type", a.org_cascade as "org_cascade", a.name as "name", a.short_name as "short_name", a.parent_id as "parent_id", a.level_num as "level_num", a.status as "status", a.order_by as "order_by", a.depart_leader as "depart_leader", a.direct_leader as "direct_leader", a.branch_leader as "branch_leader", a.remark as "remark", a.create_time as "create_time", a.create_user as "create_user", a.create_organize as "create_organize", a.update_user as "update_user", a.update_time as "update_time", ifnull(b.num,0) as "child_num", c.name as "parent_name" from system_organize a left join ( select count(*) as "num", a.parent_id as "parent_id" from system_organize a group by a.parent_id ) b on a.id=b.parent_id left join system_organize c on a.parent_id=c.id where 1=1
and a.name like concat('%',concat(#{obj.name},'%'))
and a.short_name like concat('%',concat(#{obj.short_name},'%'))
and a.org_cascade like concat('%',concat(#{obj.org_cascade},'%'))
and a.parent_id = #{obj.parent_id}
and a.type = #{obj.type}
and a.status = #{obj.status}
and a.level_num <= #{obj.level_num}
order by a.order_by+0 asc , a.create_time desc

select a.id as "id", a.type as "type", a.org_cascade as "org_cascade", a.name as "name", a.short_name as "short_name", a.parent_id as "parent_id", a.level_num as "level_num", a.status as "status", a.order_by as "order_by", a.depart_leader as "depart_leader", a.direct_leader as "direct_leader", a.branch_leader as "branch_leader", a.remark as "remark", a.create_time as "create_time", a.create_user as "create_user", a.create_organize as "create_organize", a.update_user as "update_user", a.update_time as "update_time", b.name as "parent_name", ifnull(c.num,0) as "child_num" from system_organize a left join system_organize b on a.parent_id=b.id left join ( select count(*) as "num", a.parent_id as "parent_id" from system_organize a group by a.parent_id ) c on a.id=c.parent_id where 1=1
and a.name like concat('%',concat(#{name},'%'))
and a.short_name like concat('%',concat(#{short_name},'%'))
and a.org_cascade like concat('%',concat(#{org_cascade},'%'))
and a.org_cascade like concat('%',concat((select org_cascade from system_organize where id=#{parent_cascade}),'%'))
and a.parent_id = #{parent_id}
and a.type = #{type}
and a.status = #{status}
and a.level_num <= #{level_num}
order by a.order_by+0 asc , a.create_time desc

select a.id as "id", a.name as "title", if(a.org_cascade=#{org_cascade},'0',a.parent_id) as "pid", a.org_cascade as "org_cascade" from system_organize a where 1=1
and a.org_cascade like concat('%',concat(#{org_cascade,jdbcType=VARCHAR},'%'))
and a.parent_id = #{parent_id,jdbcType=VARCHAR}
and a.type = #{type,jdbcType=VARCHAR}
order by a.order_by+0 asc, a.create_time desc

mapper.delete:
delete from system_organize_role where 1=1

    and organize_id in
    (
    select a.id from (
    select id from system_organize
        where org_cascade like
        concat('%',concat((select a.org_cascade from (select id,org_cascade from system_organize) a where a.id
        in (
        #{id}
        ) ),'%'))

    ) a
    )


    and role_id in

        #{role_id}
