mapper.select:
select a.id as "id", a.user_id as "user_id", a.organize_id as "organize_id", a.type as "type", a.position as "position", a.authOrgIds as "authOrgIds", a.authParams as "authParams", a.authOrgCascade as "authOrgCascade", a.use_status as "use_status", a.order_by as "order_by", a.create_user as "create_user", a.create_time as "create_time", a.update_time as "update_time", b.name as "organize_name" from system_user_organize a left join system_organize b on a.organize_id = b.id where 1=1
and a.user_id = #{obj.user_id}
and a.organize_id = #{obj.organize_id}
order by a.type+0 asc, a.order_by+0 asc , a.create_time desc

select a.id as "id", a.user_id as "user_id", a.organize_id as "organize_id", a.type as "type", a.position as "position", a.authOrgIds as "authOrgIds", a.authParams as "authParams", a.authOrgCascade as "authOrgCascade", a.use_status as "use_status", a.order_by as "order_by", a.create_user as "create_user", a.create_time as "create_time", a.update_time as "update_time", b.name as "organize_name", b.org_cascade as "org_cascade" from system_user_organize a left join system_organize b on a.organize_id = b.id where 1=1
and a.user_id = #{user_id}
and a.organize_id = #{organize_id}
order by a.use_status+0 asc,a.order_by+0 asc,a.type+0 asc limit 1

select a.id as "id", a.user_id as "user_id", a.organize_id as "organize_id", a.type as "type", a.position as "position", a.authOrgIds as "authOrgIds", a.authParams as "authParams", a.authOrgCascade as "authOrgCascade", a.use_status as "use_status", a.order_by as "order_by", a.create_user as "create_user", a.create_time as "create_time", a.update_time as "update_time", b.name as "organize_name", b.org_cascade as "org_cascade" from system_user_organize a left join system_organize b on a.organize_id = b.id where 1=1
and a.user_id = #{user_id}
and a.organize_id = #{organize_id}
order by a.use_status+0 asc,a.order_by+0 asc,a.type+0 asc
