mapper.select:
select a.id as "id", a.type as "type", a.dic_cascade as "dic_cascade", a.name as "name", a.short_name as "short_name", a.code as "code", a.parent_id as "parent_id", a.level_num as "level_num", a.status as "status", a.order_by as "order_by", a.remark as "remark", a.create_time as "create_time", a.create_user as "create_user", a.create_organize as "create_organize", a.update_user as "update_user", a.update_time as "update_time", b.name as "parent_name", ifnull(c.num,0) as "child_num" from system_dictionary a left join system_dictionary b on a.parent_id = b.id left join ( select count(*) as "num", a.parent_id as "parent_id" from system_dictionary a group by a.parent_id ) c on a.id=c.parent_id where 1=1
and a.name like concat('%',concat(#{obj.name},'%'))
and a.short_name like concat('%',concat(#{obj.short_name},'%'))
and a.dic_cascade like concat('%',concat(#{obj.dic_cascade},'%'))
and a.parent_id = #{obj.parent_id}
and a.type = #{obj.type}
and a.status = #{obj.status}
and a.level_num <= #{obj.level_num}
order by a.order_by+0 asc , a.create_time desc

select a.id as "id", a.type as "type", a.dic_cascade as "dic_cascade", a.name as "name", a.short_name as "short_name", a.code as "code", a.parent_id as "parent_id", a.level_num as "level_num", a.status as "status", a.order_by as "order_by", a.remark as "remark", a.create_time as "create_time", a.create_user as "create_user", a.create_organize as "create_organize", a.update_user as "update_user", a.update_time as "update_time", b.name as "parent_name", ifnull(c.num,0) as "child_num" from system_dictionary a left join system_dictionary b on a.parent_id = b.id left join ( select count(*) as "num", a.parent_id as "parent_id" from system_dictionary a group by a.parent_id ) c on a.id=c.parent_id where 1=1
and a.name like concat('%',concat(#{name},'%'))
and a.short_name like concat('%',concat(#{short_name},'%'))
and a.dic_cascade like concat('%',concat(#{dic_cascade},'%'))
and a.parent_id = #{parent_id}
and a.type = #{type}
and a.status = #{status}
and a.level_num <= #{level_num}
and a.parent_id = (select id from system_dictionary where code=#{parent_code} limit 1)
and a.code in
#{code}
and a.id in
#{id}
order by a.order_by+0 asc , a.create_time desc
